package basic;


public class OddNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=50;
		System.out.println("List of odd numbers from 25 to 50 are  :" );
		for(int i=25;i<=number;i++)
		{
			if(i%2!=0)
			{
				System.out.println(i);
			}
		}

	}

}
